package main

import (
	"fmt"
	"sort"
)

func threeSum(nums []int) [][]int {

	if len(nums) < 3 {
		return make([][]int, 0)
	}

	uniq := make([]int, 0)
	count := make([]int, 0)

	sort.Ints(nums)
	n := len(nums)
	cur := nums[0] - 1
	curCount := 1
	for i := 0; i < n; i++ {
		if nums[i] == cur {
			curCount++
		} else {
			uniq = append(uniq, cur)
			count = append(count, curCount)
			cur = nums[i]
			curCount = 1
		}
	}
	uniq = append(uniq, cur)
	count = append(count, curCount)
	uniq = uniq[1:]
	count = count[1:]
	n = len(uniq)

	rangeExisted := make([]map[int]bool, n)
	// rangeExisted[i][v] means v existed in range [i+1, n)
	for i := 0; i < n; i++ {
		rangeExisted[i] = make(map[int]bool)
		for j := i + 1; j < n; j++ {
			rangeExisted[i][uniq[j]] = true
		}
	}

	result := make([][]int, 0)
	var sum1, sum2 int
	// each number appear exactly once
	for i := 0; i < n-2; i++ {
		sum1 = uniq[i]
		if sum1 > 0 {
			break
		}

		for j := i + 1; j < n-1; j++ {
			sum2 = sum1 + uniq[j]
			if sum2 > 0 {
				break
			}
			if rangeExisted[j][0-sum2] {
				result = append(result, []int{uniq[i], uniq[j], 0 - sum2})
			}
		}
	}

	existed := make(map[int]bool, 0)
	for i := 0; i < n; i++ {
		existed[uniq[i]] = true
		// fmt.Printf("v = %d, count = %d\n", uniq[i], count[i])
	}

	// some number repeat 2 times
	for i := 0; i < n; i++ {
		if count[i] >= 2 {
			sum2 = uniq[i] + uniq[i]
			if existed[0-sum2] {
				if uniq[i] == 0 {
					// 0 repeat 3 times
					if count[i] >= 3 {
						result = append(result, []int{0, 0, 0})
					}
				} else {
					result = append(result, []int{uniq[i], uniq[i], 0 - sum2})
				}
			}
		}
	}

	return result
}

