	fmt.Println(threeSum([]int{-1, 0, 1, 2, -1, 4}))
	fmt.Println(threeSum([]int{0, 0, 0, 0}))
	fmt.Println(threeSum([]int{-1, -1, 2, -4, 2}))
	fmt.Println(threeSum([]int{-1, 2, -4, 2}))
	fmt.Println(threeSum([]int{-1, -1, 2, -4}))
